import React, { useState, useEffect } from "react";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import axios from "axios";
// import { Button } from "@progress/kendo-react-buttons";
import { PDFExport, savePDF } from "@progress/kendo-react-pdf";
import { useRef } from "react";
import Button from "@mui/material/Button";

export default function Report() {
  const [doctors, setDoctors] = useState([]);

  const getRequest = () => {
    axios.get("http://localhost:4000/ambulance").then((response) => {
      console.log(response.data);
      setDoctors(response.data);
    });
  };

  useEffect(() => {
    getRequest();
  }, [doctors]);

  const PDFExportComponent = useRef(null);
  const pdfonclick = (e) => {
    PDFExportComponent.current.save();
  };

  return (
    <Paper sx={{ width: "100%", overflow: "hidden" }}>
      <Button
        variant="outlined"
        onClick={pdfonclick}
        style={{ padding: "5px 700px 5px 700px" }}
      >
        Export
      </Button>
      <PDFExport ref={PDFExportComponent}>
        <div>
          <div
            className="row"
            style={{
              paddingLeft: "50px",
              paddingRight: "50px",
              paddingTop: "20px",
              paddingBottom: "20px",
            }}
          >
            <div className="column" style={{ float: "left", width: "50%" }}>
              <img src="/images/logo.png" width="40%" />
            </div>
            <div
              className="column"
              style={{ float: "left", width: "50%", textAlign: "right" }}
            >
              <span>info@Healistry.com</span>
              <br />
              <span style={{ color: "#4ad7d1" }}>WWW.Healistry.com</span>
              <br />
              <span>011 1111 543</span>
            </div>
          </div>
          <div style={{ textAlign: "center", background: "#b6b7b8" }}>
            <div style={{ paddingBottom: "100px" }}>
              <span style={{ fontSize: "70px" }}>
                <span style={{ fontWeight: "bold", color: "#03807b" }}>
                  Annual Report
                </span>
                <br />
                Healistry Doctors Report
              </span>
            </div>
            <div style={{ textAlign: "center" }}>
              <center>
                <img src="/images/report.png" width="50%" />
              </center>
            </div>
          </div>
        </div>
        <TableContainer>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                <TableCell style={{ fontWeight: "bold" }}>Ambulance Name</TableCell>
                <TableCell align="right" style={{ fontWeight: "bold" }}>
                  Conatact Number
                </TableCell>
                <TableCell align="right" style={{ fontWeight: "bold" }}>
                  Address
                </TableCell>
                <TableCell align="right" style={{ fontWeight: "bold" }}>
                No of ambulance
                </TableCell>
                <TableCell align="right" style={{ fontWeight: "bold" }}>
                Registered since
                </TableCell>
                <TableCell align="right" style={{ fontWeight: "bold" }}>
                Duty hours
                </TableCell>
                <TableCell align="right" style={{ fontWeight: "bold" }}>
                Duty Hospitals
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {doctors.map((item) => (
                <TableRow key={item.id}>
                  <TableCell component="th" scope="row">
                    {item.name}
                  </TableCell>
                  <TableCell align="right">{item.location}</TableCell>
                  <TableCell align="right">{item.hospital}</TableCell>
                  <TableCell align="right">{item.contact}</TableCell>
                  <TableCell align="right">{item.dutyhours}</TableCell>
                  <TableCell align="right">{item.numberofambulance}</TableCell>
                  <TableCell align="right">{item.since}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </PDFExport>
    </Paper>
  );
}
